import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Link } from "react-router-dom";
import { ArrowRight } from "lucide-react";
import { courses as initialCourses, categories } from "@/lib/constants";
import { supabase } from "@/lib/supabase";

const CourseSection = () => {
  const [courses, setCourses] = useState<any[]>(initialCourses);
  const [loading, setLoading] = useState(true);
  const [activeIndex, setActiveIndex] = useState(0);
  const [activeCategory, setActiveCategory] = useState<string>("All");
  const [showMobileDetail, setShowMobileDetail] = useState(false);

  useEffect(() => {
    const fetchCourses = async () => {
      const { data, error } = await supabase
        .from('courses')
        .select('*')
        .order('display_order', { ascending: true });

      if (data && !error) {
        setCourses(data);
      } else {
        setCourses(initialCourses); // Fallback to initial data if error
      }
      setLoading(false);
    };

    fetchCourses();

    const handleCourseSelect = (e: CustomEvent<{ title: string }>) => {
      const index = (courses.length > 0 ? courses : initialCourses).findIndex(c => c.title === e.detail.title);
      if (index !== -1) {
        setActiveCategory("All"); // Reset category to show all so index is correct
        setTimeout(() => {
          setActiveIndex(index);
          // Auto open detail view on mobile if course is selected from elsewhere (e.g. hero)
          if (window.innerWidth < 768) {
            setShowMobileDetail(true);
          }
        }, 0);
      }
    };

    window.addEventListener('courseSelected' as any, handleCourseSelect);
    return () => window.removeEventListener('courseSelected' as any, handleCourseSelect);
  }, []);

  const filtered = activeCategory === "All" ? courses : courses.filter((c) => c.category === activeCategory);
  const activeCourse = filtered[activeIndex] || filtered[0];

  const handleCategoryChange = (cat: string) => {
    setActiveCategory(cat);
    setActiveIndex(0);
  };

  const handleCourseClick = (index: number) => {
    setActiveIndex(index);
    if (window.innerWidth < 768) {
      setShowMobileDetail(true);
    }
  };

  return (
    <section id="courses" className="section-light py-20 md:py-32 overflow-hidden">
      <div className="max-w-7xl mx-auto px-6">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          className="text-center mb-10 md:mb-12"
        >
          <p className="text-accent font-semibold tracking-widest uppercase text-xs md:text-sm mb-2 md:mb-3">Our Programs</p>
          <h2 className="font-display text-3xl md:text-5xl font-bold text-foreground">
            Career-Focused <span className="text-gradient-gold">Courses</span>
          </h2>
        </motion.div>

        {/* --- MOBILE VIEW: LIST -> DETAIL --- */}
        <div className="block md:hidden relative">
          <AnimatePresence mode="wait">
            {!showMobileDetail ? (
              /* MOBILE STEP 1: Course List */
              <motion.div
                key="list"
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                transition={{ duration: 0.3 }}
                className="space-y-6"
              >
                {/* Horizontal Categories */}
                <div className="flex overflow-x-auto pb-4 gap-2 no-scrollbar">
                  {categories.map((cat) => (
                    <button
                      key={cat}
                      onClick={() => handleCategoryChange(cat)}
                      className={`whitespace-nowrap px-4 py-2 rounded-full text-xs font-semibold transition-all duration-300 ${activeCategory === cat
                        ? "bg-accent text-accent-foreground gold-glow-box"
                        : "border border-border text-muted-foreground"
                        }`}
                    >
                      {cat}
                    </button>
                  ))}
                </div>

                <div className="space-y-4">
                  {filtered.map((course, i) => (
                    <button
                      key={course.title}
                      onClick={() => handleCourseClick(i)}
                      className="w-full text-left p-5 rounded-2xl bg-primary border border-white/10 active:scale-[0.98] transition-all duration-200 group relative"
                    >
                      <div className="flex justify-between items-start mb-2">
                        <span className="text-[10px] uppercase tracking-widest text-accent/80 font-bold">{course.category}</span>
                        {course.featured && (
                          <span className="text-[9px] uppercase tracking-wider bg-accent/20 text-accent px-2 py-0.5 rounded-full font-bold">
                            Popular
                          </span>
                        )}
                      </div>
                      <h3 className="font-display text-lg font-bold text-white mb-2">{course.title}</h3>
                      <div className="flex items-center gap-3 text-white/50 text-xs">
                        <span>{course.duration}</span>
                        <span className="w-1 h-1 rounded-full bg-white/20" />
                        <span className="text-accent/90">{course.salary}</span>
                      </div>
                      <div className="absolute right-5 top-1/2 -translate-y-1/2 w-8 h-8 rounded-full bg-white/5 flex items-center justify-center">
                        <ArrowRight size={14} className="text-accent" />
                      </div>
                    </button>
                  ))}
                </div>
              </motion.div>
            ) : (
              /* MOBILE STEP 2: Course Detail */
              <motion.div
                key="detail"
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 20 }}
                transition={{ duration: 0.4, ease: "easeInOut" }}
                className="bg-background rounded-3xl border border-border overflow-hidden min-h-[500px] flex flex-col"
              >
                {/* Mobile Detail Header */}
                <div className="p-6 pb-2">
                  <button
                    onClick={() => setShowMobileDetail(false)}
                    className="flex items-center gap-2 text-accent font-semibold text-sm mb-6 active:translate-x-[-4px] transition-transform"
                  >
                    <ArrowRight size={16} className="rotate-180" />
                    Back to List
                  </button>

                  {activeCourse && (
                    <>
                      <div className="flex items-center gap-2 mb-2">
                        <span className="text-[10px] uppercase tracking-widest text-accent font-bold">{activeCourse.category}</span>
                        <span className="w-6 h-px bg-accent/30" />
                      </div>
                      <h3 className="font-display text-2xl font-bold text-foreground mb-4 leading-tight">
                        {activeCourse.title}
                      </h3>
                      <div className="h-1 w-12 bg-accent rounded-full mb-6" />
                    </>
                  )}
                </div>

                <div className="p-6 pt-0 flex-1 space-y-6">
                  <p className="text-muted-foreground text-sm leading-relaxed">
                    {activeCourse.overview}
                  </p>

                  <div className="space-y-3">
                    <h4 className="text-[10px] uppercase tracking-widest text-foreground font-bold opacity-50">Technologies</h4>
                    <div className="flex flex-wrap gap-1.5">
                      {activeCourse.tools.map((t) => (
                        <span key={t} className="px-2.5 py-1 bg-primary text-primary-foreground text-[10px] rounded-lg font-medium">
                          {t}
                        </span>
                      ))}
                    </div>
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div className="p-3 rounded-xl bg-accent/5 border border-accent/10">
                      <div className="text-[9px] text-muted-foreground uppercase mb-1">Duration</div>
                      <div className="font-display font-bold text-foreground text-xs">{activeCourse.duration}</div>
                    </div>
                    <div className="p-3 rounded-xl bg-accent/5 border border-accent/10">
                      <div className="text-[9px] text-muted-foreground uppercase mb-1">Salary Range</div>
                      <div className="font-display font-bold text-accent text-xs">{activeCourse.salary}</div>
                    </div>
                  </div>

                  <div className="p-3 rounded-xl bg-accent/5 border border-accent/10">
                    <div className="text-[9px] text-muted-foreground uppercase mb-1">Top Role</div>
                    <div className="font-display font-bold text-foreground text-xs">{activeCourse.roles.split(", ")[0]}</div>
                  </div>
                </div>

                {/* Mobile Sticky CTA */}
                <div className="p-4 bg-background border-t border-border mt-auto">
                  <Link
                    to="/apply"
                    className="flex items-center justify-center gap-2 w-full py-4 bg-accent text-accent-foreground rounded-2xl font-bold text-sm gold-glow-box shadow-xl active:scale-[0.98] transition-all"
                  >
                    Enroll Now <ArrowRight size={16} />
                  </Link>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>

        {/* --- DESKTOP VIEW: SPLIT PANEL (UNCHANGED) --- */}
        <div className="hidden md:grid lg:grid-cols-[1fr_1.3fr] gap-0 border border-border rounded-2xl overflow-hidden min-h-[480px]">
          {/* Left: Course list */}
          <div className="bg-primary p-2 md:p-4 space-y-1 overflow-y-auto max-h-[520px]">
            {filtered.map((course, i) => (
              <button
                key={course.title}
                onClick={() => setActiveIndex(i)}
                className={`w-full text-left px-5 py-4 rounded-xl transition-all duration-300 group relative ${activeIndex === i
                  ? "bg-secondary border border-accent/30"
                  : "hover:bg-secondary/50"
                  }`}
              >
                {activeIndex === i && (
                  <motion.div
                    layoutId="course-indicator"
                    className="absolute left-0 top-1/2 -translate-y-1/2 w-1 h-8 bg-accent rounded-r-full"
                  />
                )}
                <span
                  className={`font-display text-base font-bold transition-colors duration-200 ${activeIndex === i ? "text-accent" : "text-primary-foreground/80 group-hover:text-primary-foreground"
                    }`}
                >
                  {course.title}
                </span>
                {course.featured && (
                  <span className="ml-2 text-[10px] uppercase tracking-wider bg-accent/20 text-accent px-2 py-0.5 rounded-full font-bold">
                    Popular
                  </span>
                )}
                <p className="text-primary-foreground/40 text-xs mt-0.5">{course.duration} Â· {course.salary}</p>
              </button>
            ))}
          </div>

          {/* Right: Course details */}
          <div className="relative bg-background p-8 md:p-12 flex flex-col justify-center overflow-hidden">
            {/* Large faded background text */}
            <div className="absolute top-1/2 right-4 -translate-y-1/2 font-display text-[120px] md:text-[180px] font-bold text-foreground/[0.08] leading-none select-none pointer-events-none whitespace-nowrap">
              {activeCourse?.shortTitle}
            </div>

            <AnimatePresence mode="wait">
              {activeCourse && (
                <motion.div
                  key={activeCourse.title}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -15 }}
                  transition={{ duration: 0.3 }}
                  className="relative z-10"
                >
                  <div className="flex items-center gap-3 mb-4">
                    <span className="text-xs uppercase tracking-widest text-accent font-semibold">{activeCourse.category}</span>
                    <span className="w-8 h-px bg-accent" />
                  </div>

                  <h3 className="font-display text-2xl md:text-4xl font-bold text-foreground mb-4">
                    {activeCourse.title}
                  </h3>

                  <motion.div
                    initial={{ width: 0 }}
                    animate={{ width: "4rem" }}
                    transition={{ duration: 0.4, delay: 0.15 }}
                    className="h-1 bg-accent rounded-full mb-6"
                  />

                  <p className="text-muted-foreground text-base md:text-lg leading-relaxed mb-6 max-w-lg">
                    {activeCourse.overview}
                  </p>

                  <div className="flex flex-wrap gap-2 mb-8">
                    {activeCourse.tools.map((tool: string) => (
                      <span
                        key={tool}
                        className="px-3 py-1 bg-white/5 border border-white/10 rounded-full text-[10px] text-white/60 font-medium"
                      >
                        {tool}
                      </span>
                    ))}
                  </div>

                  <div className="grid grid-cols-3 gap-4 mb-8">
                    <div>
                      <div className="text-xs text-muted-foreground uppercase tracking-wider mb-1">Duration</div>
                      <div className="font-display font-bold text-foreground">{activeCourse.duration}</div>
                    </div>
                    <div>
                      <div className="text-xs text-muted-foreground uppercase tracking-wider mb-1">Salary Range</div>
                      <div className="font-display font-bold text-accent">{activeCourse.salary}</div>
                    </div>
                    <div>
                      <div className="text-xs text-muted-foreground uppercase tracking-wider mb-1">Top Role</div>
                      <div className="font-display font-bold text-foreground text-sm">{activeCourse.roles.split(", ")[0]}</div>
                    </div>
                  </div>

                  <Link
                    to="/apply"
                    className="inline-flex items-center gap-2 bg-accent text-accent-foreground px-6 py-3 rounded-lg font-semibold hover-scale gold-glow-box transition-all duration-300"
                  >
                    Enroll Now <ArrowRight size={16} />
                  </Link>
                </motion.div>
              )}
            </AnimatePresence>
          </div>
        </div>
      </div>
    </section>
  );
};

export default CourseSection;

import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { Briefcase, Building2, Sparkles } from "lucide-react";
import { supabase } from "@/lib/supabase";

const HiringPartnersSection = () => {
    const [partners, setPartners] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchPartners = async () => {
            const { data, error } = await supabase
                .from('hiring_partners')
                .select('*')
                .order('display_order', { ascending: true });

            if (data && !error) {
                setPartners(data);
            }
            setLoading(false);
        };

        fetchPartners();
    }, []);

    if (!loading && partners.length === 0) return null;

    return (
        <section className="relative py-24 md:py-32 bg-navy-deep overflow-hidden">
            {/* Background elements */}
            <div className="absolute top-0 left-0 w-full h-full pointer-events-none opacity-20">
                <div className="absolute top-[10%] left-[5%] w-96 h-96 bg-accent rounded-full blur-[120px] mix-blend-screen animate-pulse" />
                <div className="absolute bottom-[10%] right-[5%] w-80 h-80 bg-blue-500 rounded-full blur-[100px] opacity-30 mix-blend-screen" />
            </div>

            <div className="container relative z-10 mx-auto px-4 md:px-6">
                <div className="flex flex-col items-center mb-16">
                    <motion.div
                        initial={{ opacity: 0, scale: 0.9 }}
                        whileInView={{ opacity: 1, scale: 1 }}
                        viewport={{ once: true }}
                        className="inline-flex items-center gap-2 px-4 py-2 rounded-full bg-accent/10 border border-accent/20 mb-6"
                    >
                        <Briefcase size={14} className="text-accent" />
                        <span className="text-accent text-xs font-bold uppercase tracking-widest">Industry Connections</span>
                    </motion.div>

                    <h2 className="font-display text-4xl md:text-6xl font-bold text-white text-center mb-6">
                        Our <span className="text-gradient-gold">Hiring Partners</span>
                    </h2>

                    <p className="text-white/60 text-center max-w-2xl text-lg">
                        Our students are recruited by some of the most innovative companies in the industry.
                    </p>
                </div>

                {loading ? (
                    <div className="flex justify-center py-12">
                        <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-accent border-solid" />
                    </div>
                ) : (
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-8 md:gap-12">
                        {partners.map((partner, index) => (
                            <motion.div
                                key={partner.id}
                                initial={{ opacity: 0, y: 20 }}
                                whileInView={{ opacity: 1, y: 0 }}
                                viewport={{ once: true }}
                                transition={{ delay: index * 0.1 }}
                                className="group relative"
                            >
                                <div className="bg-white/5 border border-white/10 p-8 rounded-3xl h-40 flex items-center justify-center backdrop-blur-sm hover:border-accent/30 hover:bg-white/10 transition-all duration-500 hover:-translate-y-2">
                                    {partner.logo_url ? (
                                        <img
                                            src={partner.logo_url}
                                            alt={partner.name}
                                            className="max-h-16 w-auto object-contain grayscale group-hover:grayscale-0 transition-all duration-500"
                                        />
                                    ) : (
                                        <div className="flex flex-col items-center gap-2">
                                            <Building2 className="text-white/20 group-hover:text-accent transition-colors" size={32} />
                                            <span className="text-white/40 font-bold text-sm tracking-wider">{partner.name}</span>
                                        </div>
                                    )}

                                    {/* Hover glow */}
                                    <div className="absolute inset-0 bg-accent/5 rounded-3xl opacity-0 group-hover:opacity-100 transition-opacity blur-xl" />
                                </div>
                            </motion.div>
                        ))}
                    </div>
                )}

                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    className="mt-20 pt-10 border-t border-white/5 text-center"
                >
                    <div className="inline-flex items-center gap-2 text-white/40">
                        <Sparkles size={16} className="text-accent/40" />
                        <span className="text-sm font-medium">Join 50+ partners hiring our graduates every month</span>
                    </div>
                </motion.div>
            </div>
        </section>
    );
};

export default HiringPartnersSection;

import { useRef, useState, useEffect } from "react";
import { motion, useAnimationFrame } from "framer-motion";
import { Sparkles, MapPin, Building2, CheckCircle } from "lucide-react";
import { placedStudentsData as initialStudents } from "@/lib/constants";
import { supabase } from "@/lib/supabase";

const DESKTOP_CARD_WIDTH = 280;
const DESKTOP_CARD_GAP = 24;
const MOBILE_CARD_WIDTH = 150;
const MOBILE_CARD_GAP = 14;
const SPEED = 0.55;

function useCardDimensions() {
    const [isMobile, setIsMobile] = useState(
        typeof window !== "undefined" ? window.innerWidth < 640 : false
    );
    useEffect(() => {
        const handler = () => setIsMobile(window.innerWidth < 640);
        window.addEventListener("resize", handler);
        return () => window.removeEventListener("resize", handler);
    }, []);
    return isMobile
        ? { cardWidth: MOBILE_CARD_WIDTH, cardGap: MOBILE_CARD_GAP }
        : { cardWidth: DESKTOP_CARD_WIDTH, cardGap: DESKTOP_CARD_GAP };
}

const PlacementCard = ({
    student,
    status,
    cardWidth,
    cardGap,
}: {
    student: any;
    status: "active" | "faded";
    cardWidth: number;
    cardGap: number;
}) => {
    const isMobile = cardWidth < 200;

    return (
        <div
            className="flex-shrink-0 relative transition-all duration-500"
            style={{
                width: cardWidth,
                paddingTop: isMobile ? "2.5rem" : "4rem",
            }}
        >
            <div className="absolute top-0 left-1/2 -translate-x-1/2 z-20">
                <div
                    className={`${isMobile ? "w-16 h-16" : "w-28 h-28"} rounded-full overflow-hidden border-[3px] shadow-2xl`}
                    style={{
                        borderColor: "rgba(212,175,55,0.35)",
                        boxShadow: "0 8px 30px rgba(0,0,0,0.5), 0 0 0 4px rgba(212,175,55,0.08)",
                    }}
                >
                    <img
                        src={student.image_url}
                        alt={student.name}
                        className="w-full h-full object-cover object-center"
                        draggable={false}
                    />
                </div>
                <div className="absolute -bottom-1 -right-1 bg-emerald-500 rounded-full p-1 border-2 border-[#0b1c2d] shadow-lg">
                    <CheckCircle size={11} className="text-white" />
                </div>
            </div>

            <div
                className={`rounded-[20px] border border-white/[0.08] flex flex-col items-center text-center ${isMobile ? "gap-1.5 px-3 pt-10 pb-4" : "gap-3 px-6 pt-16 pb-8"}`}
                style={{
                    background: "rgba(255,255,255,0.04)",
                    backdropFilter: "blur(14px)",
                    boxShadow: "0 20px 60px rgba(0,0,0,0.35), inset 0 1px 0 rgba(255,255,255,0.06)",
                }}
            >
                <h3 className={`text-white font-bold leading-tight tracking-tight ${isMobile ? "text-[11px]" : "text-lg"}`}>
                    {student.name}
                </h3>
                <div className="flex items-center gap-1">
                    <Building2 size={isMobile ? 10 : 13} className="text-accent flex-shrink-0" />
                    <span className={`text-accent font-semibold tracking-wide ${isMobile ? "text-[10px]" : "text-sm"}`}>
                        {student.company}
                    </span>
                </div>
                <div className="flex items-center gap-1">
                    <MapPin size={isMobile ? 9 : 12} className="text-white/40 flex-shrink-0" />
                    <span className={`text-white/50 font-medium ${isMobile ? "text-[9px]" : "text-xs"}`}>
                        {student.location}
                    </span>
                </div>
                <div className="mt-1 pt-3 border-t border-white/[0.07] w-full">
                    <span className="text-[10px] font-bold uppercase tracking-[0.18em] text-white/25">
                        Successfully Placed
                    </span>
                </div>
            </div>
        </div>
    );
};

const TestimonialSection = () => {
    const [placedStudents, setPlacedStudents] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const trackRef = useRef<HTMLDivElement>(null);
    const xRef = useRef(0);
    const isPausedRef = useRef(false);
    const [, forceUpdate] = useState(0);

    useEffect(() => {
        const fetchStudents = async () => {
            const { data, error } = await supabase
                .from('testimonials')
                .select('*')
                .order('display_order', { ascending: true });

            if (data && !error && data.length > 0) {
                setPlacedStudents(data);
            } else {
                setPlacedStudents(initialStudents);
            }
            setLoading(false);
        };
        fetchStudents();
    }, []);

    const { cardWidth, cardGap } = useCardDimensions();
    const cardTotal = cardWidth + cardGap;
    const items = placedStudents.length > 0 ? [...placedStudents, ...placedStudents, ...placedStudents] : [];
    const totalWidth = placedStudents.length * cardTotal;

    useAnimationFrame(() => {
        if (isPausedRef.current || loading || placedStudents.length === 0) return;
        xRef.current -= SPEED;
        if (Math.abs(xRef.current) >= totalWidth) {
            xRef.current += totalWidth;
        }
        if (trackRef.current) {
            trackRef.current.style.transform = `translateX(${xRef.current}px)`;
        }
        forceUpdate((n) => n + 1);
    });

    return (
        <section className="relative py-24 md:py-32 overflow-hidden bg-navy-deep">
            <div className="absolute inset-0 pointer-events-none overflow-hidden">
                <div
                    className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-[900px] h-[500px] rounded-full blur-[120px] opacity-25"
                    style={{ background: "radial-gradient(ellipse, rgba(212,175,55,0.2) 0%, transparent 70%)" }}
                />
            </div>

            <div className="max-w-7xl mx-auto px-6 relative z-10 mb-20 text-center">
                <motion.div
                    initial={{ opacity: 0, y: 15 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    className="inline-flex items-center gap-2 px-4 py-2 rounded-full bg-accent/10 border border-accent/20 mb-6"
                >
                    <Sparkles size={13} className="text-accent" />
                    <span className="text-accent text-[11px] font-bold uppercase tracking-[0.2em]">Placement Success</span>
                </motion.div>
                <motion.h2
                    initial={{ opacity: 0, y: 15 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    transition={{ delay: 0.1 }}
                    className="font-display text-4xl md:text-6xl font-bold text-white leading-tight tracking-tight"
                >
                    Career <span className="text-gradient-gold">Transformations</span>
                </motion.h2>
                <motion.p
                    initial={{ opacity: 0, y: 10 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    transition={{ delay: 0.2 }}
                    className="text-white/40 text-sm mt-4 max-w-md mx-auto"
                >
                    Real students. Real placements. An endless stream of success.
                </motion.p>
            </div>

            <div
                className="relative w-full overflow-hidden"
                style={{ paddingTop: "4.5rem", paddingBottom: "2.5rem" }}
                onMouseEnter={() => { isPausedRef.current = true; }}
                onMouseLeave={() => { isPausedRef.current = false; }}
            >
                <div className="absolute left-0 top-0 h-full w-[160px] z-10 pointer-events-none" style={{ background: "linear-gradient(to right, #0b1c2d 0%, transparent 100%)" }} />
                <div className="absolute right-0 top-0 h-full w-[160px] z-10 pointer-events-none" style={{ background: "linear-gradient(to left, #0b1c2d 0%, transparent 100%)" }} />

                <div
                    ref={trackRef}
                    className="flex"
                    style={{ gap: cardGap, willChange: "transform", backfaceVisibility: "hidden" }}
                >
                    {items.map((student, index) => (
                        <PlacementCard
                            key={`${student.name}-${index}`}
                            student={student}
                            status="active"
                            cardWidth={cardWidth}
                            cardGap={cardGap}
                        />
                    ))}
                </div>
            </div>
        </section>
    );
};

export default TestimonialSection;

import { useState, useEffect } from "react";
import { supabase } from "@/lib/supabase";
import {
    Table,
    TableBody,
    TableCell,
    TableHead,
    TableHeader,
    TableRow
} from "@/components/ui/table";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
    Dialog,
    DialogContent,
    DialogHeader,
    DialogTitle,
    DialogFooter
} from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { Plus, Edit2, Trash2, Save, Loader2, Building2, Globe, Image as ImageIcon } from "lucide-react";

interface HiringPartner {
    id: string;
    name: string;
    logo_url: string;
    website_url: string;
    display_order: number;
}

const AdminHiringPartners = () => {
    const [partners, setPartners] = useState<HiringPartner[]>([]);
    const [loading, setLoading] = useState(true);
    const [isDialogOpen, setIsDialogOpen] = useState(false);
    const [editingPartner, setEditingPartner] = useState<HiringPartner | null>(null);
    const [isSaving, setIsSaving] = useState(false);
    const { toast } = useToast();

    const [formData, setFormData] = useState<Partial<HiringPartner>>({
        name: "",
        logo_url: "",
        website_url: "",
        display_order: 0,
    });

    useEffect(() => {
        fetchPartners();
    }, []);

    const fetchPartners = async () => {
        setLoading(true);
        const { data, error } = await supabase
            .from("hiring_partners")
            .select("*")
            .order("display_order", { ascending: true });

        if (error) {
            toast({ variant: "destructive", title: "Error fetching partners", description: error.message });
        } else {
            setPartners(data || []);
        }
        setLoading(false);
    };

    const handleOpenDialog = (partner: HiringPartner | null = null) => {
        if (partner) {
            setEditingPartner(partner);
            setFormData(partner);
        } else {
            setEditingPartner(null);
            setFormData({
                name: "",
                logo_url: "",
                website_url: "",
                display_order: partners.length + 1,
            });
        }
        setIsDialogOpen(true);
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setIsSaving(true);

        const { id, ...dataToSave } = formData as HiringPartner;

        let error;
        if (editingPartner) {
            const { error: updateError } = await supabase
                .from("hiring_partners")
                .update(dataToSave)
                .eq("id", editingPartner.id);
            error = updateError;
        } else {
            const { error: insertError } = await supabase
                .from("hiring_partners")
                .insert([dataToSave]);
            error = insertError;
        }

        if (error) {
            toast({ variant: "destructive", title: "Error saving partner", description: error.message });
        } else {
            toast({ title: editingPartner ? "Partner updated" : "Partner created" });
            setIsDialogOpen(false);
            fetchPartners();
        }
        setIsSaving(false);
    };

    const handleDelete = async (id: string) => {
        if (!confirm("Are you sure you want to remove this hiring partner?")) return;

        setLoading(true);
        const { error } = await supabase.from("hiring_partners").delete().eq("id", id);

        if (error) {
            toast({ variant: "destructive", title: "Error deleting partner", description: error.message });
        } else {
            toast({ title: "Partner removed" });
            fetchPartners();
        }
        setLoading(false);
    };

    return (
        <div className="space-y-6">
            <div className="flex justify-between items-center">
                <div>
                    <h1 className="text-3xl font-bold font-display text-white">Hiring Partners</h1>
                    <p className="text-white/40 text-sm mt-1">Manage partner companies and their logos</p>
                </div>
                <Button onClick={() => handleOpenDialog()} className="bg-accent hover:bg-gold text-navy font-bold gap-2">
                    <Plus size={18} /> Add New Partner
                </Button>
            </div>

            <div className="bg-white/5 border border-white/10 rounded-2xl overflow-hidden backdrop-blur-sm">
                {loading && !isDialogOpen ? (
                    <div className="flex items-center justify-center py-20">
                        <Loader2 className="animate-spin text-accent" size={40} />
                    </div>
                ) : (
                    <Table>
                        <TableHeader className="bg-white/5">
                            <TableRow className="border-white/10 hover:bg-transparent">
                                <TableHead className="text-white/60 w-20">Order</TableHead>
                                <TableHead className="text-white/60">Partner</TableHead>
                                <TableHead className="text-white/60">Logo URL</TableHead>
                                <TableHead className="text-white/60">Website</TableHead>
                                <TableHead className="text-white/60 text-right">Actions</TableHead>
                            </TableRow>
                        </TableHeader>
                        <TableBody>
                            {partners.length === 0 ? (
                                <TableRow>
                                    <TableCell colSpan={5} className="text-center py-12 text-white/20">
                                        No hiring partners found. Add your first partner to get started.
                                    </TableCell>
                                </TableRow>
                            ) : (
                                partners.map((partner) => (
                                    <TableRow key={partner.id} className="border-white/5 hover:bg-white/5 transition-colors">
                                        <TableCell className="font-mono text-white/40">{partner.display_order}</TableCell>
                                        <TableCell>
                                            <div className="flex items-center gap-3">
                                                {partner.logo_url && (
                                                    <div className="w-8 h-8 rounded bg-white/10 flex items-center justify-center p-1 overflow-hidden">
                                                        <img src={partner.logo_url} alt={partner.name} className="max-w-full max-h-full object-contain" />
                                                    </div>
                                                )}
                                                <span className="font-bold text-white">{partner.name}</span>
                                            </div>
                                        </TableCell>
                                        <TableCell className="text-white/40 max-w-[200px] truncate font-mono text-xs">
                                            {partner.logo_url}
                                        </TableCell>
                                        <TableCell className="text-white/60 truncate max-w-[200px]">
                                            {partner.website_url ? (
                                                <a href={partner.website_url} target="_blank" rel="noopener noreferrer" className="hover:text-accent underline inline-flex items-center gap-1">
                                                    {partner.website_url} <Globe size={10} />
                                                </a>
                                            ) : "-"}
                                        </TableCell>
                                        <TableCell className="text-right">
                                            <div className="flex justify-end gap-2">
                                                <Button variant="ghost" size="icon" onClick={() => handleOpenDialog(partner)} className="hover:bg-white/10 text-white/60 hover:text-white">
                                                    <Edit2 size={16} />
                                                </Button>
                                                <Button variant="ghost" size="icon" onClick={() => handleDelete(partner.id)} className="hover:bg-destructive/10 text-white/40 hover:text-destructive">
                                                    <Trash2 size={16} />
                                                </Button>
                                            </div>
                                        </TableCell>
                                    </TableRow>
                                ))
                            )}
                        </TableBody>
                    </Table>
                )}
            </div>

            <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
                <DialogContent className="bg-secondary border-white/10 text-white max-w-md">
                    <DialogHeader>
                        <DialogTitle className="text-2xl font-bold font-display text-accent">
                            {editingPartner ? "Edit Partner" : "Add New Partner"}
                        </DialogTitle>
                    </DialogHeader>
                    <form onSubmit={handleSubmit} className="space-y-6 py-4">
                        <div className="space-y-4">
                            <div className="space-y-2">
                                <label className="text-sm font-semibold text-white/60 flex items-center gap-2">
                                    <Building2 size={14} /> Company Name
                                </label>
                                <Input
                                    value={formData.name}
                                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                                    placeholder="e.g. Google"
                                    className="bg-white/5 border-white/10 text-white"
                                    required
                                />
                            </div>

                            <div className="space-y-2">
                                <label className="text-sm font-semibold text-white/60 flex items-center gap-2">
                                    <ImageIcon size={14} /> Logo URL
                                </label>
                                <Input
                                    value={formData.logo_url}
                                    onChange={(e) => setFormData({ ...formData, logo_url: e.target.value })}
                                    placeholder="https://example.com/logo.png"
                                    className="bg-white/5 border-white/10 text-white"
                                />
                                <p className="text-[10px] text-white/30">Provide a direct URL to a transparent PNG/SVG logo</p>
                            </div>

                            <div className="space-y-2">
                                <label className="text-sm font-semibold text-white/60 flex items-center gap-2">
                                    <Globe size={14} /> Website URL (Optional)
                                </label>
                                <Input
                                    value={formData.website_url}
                                    onChange={(e) => setFormData({ ...formData, website_url: e.target.value })}
                                    placeholder="https://google.com"
                                    className="bg-white/5 border-white/10 text-white"
                                />
                            </div>

                            <div className="space-y-2">
                                <label className="text-sm font-semibold text-white/60">Display Order</label>
                                <Input
                                    type="number"
                                    value={formData.display_order}
                                    onChange={(e) => setFormData({ ...formData, display_order: parseInt(e.target.value) })}
                                    className="bg-white/5 border-white/10 text-white"
                                />
                            </div>
                        </div>

                        <DialogFooter className="pt-6">
                            <Button type="button" variant="ghost" onClick={() => setIsDialogOpen(false)} className="text-white/60 hover:text-white">
                                Cancel
                            </Button>
                            <Button type="submit" className="bg-accent hover:bg-gold text-navy font-bold gap-2 min-w-[120px]" disabled={isSaving}>
                                {isSaving ? <Loader2 className="animate-spin" size={18} /> : <Save size={18} />}
                                {editingPartner ? "Update Partner" : "Save Partner"}
                            </Button>
                        </DialogFooter>
                    </form>
                </DialogContent>
            </Dialog>
        </div>
    );
};

export default AdminHiringPartners;

